/**
 * Cbor utility module to
 * 1) provide a clean import (import * as cbor from './cbor';) and
 * 2) provide a consistent Uint8Array interface, since cbor-js uses ArrayBuffers.
 */
export declare function encode(input: any): Uint8Array;
export declare function decode(input: Uint8Array): any;
declare const cbor: {
    encode: typeof encode;
    decode: typeof decode;
};
export declare class CborDecodeError extends Error {
    private data;
    constructor(data: Uint8Array, ...params: any[]);
}
export default cbor;
